mapper.select:
SELECT MIN(BLN) AS BLN
FROM  CLINIC
WHERE CID = #{CID}

SELECT BLN
FROM EMP
WHERE EID = #{CID}

SELECT CNAME
FROM CEO
WHERE CID = #{CID}

SELECT ENAME
FROM EMP
WHERE EID = #{CID}

SELECT NVL(COUNT(*),0)
FROM CEO
WHERE CID = #{CID}

mapper.update:
update book_list
set aprvl_yn = #{aprvl}
where bseq = #{bseq}

mapper.select:
select count(*) cnt
from ceo
where cid =#{cId}
and pw = #{pass}

select count(*) cnt
from emp
where EID = #{cId}
and pw = #{pass}

mapper.insert:
update QUESTION_GC
set reply = #{reply}, rstatus = 'Y', rdate = sysdate ,rid = #{rid}, rip = #{rip}
where qgc_seq = #{qgc_seq}

mapper.select:
select cid
from ceo
where cname = #{cName} 
and tel = #{cPhone}
and birthday = #{cBirth}

select eid
from emp
where ename = #{cName}
and tel = #{cPhone}
and birthday = #{cBirth}

select cid
from ceo
where cname = #{cName} 
and tel = #{cPhone}
and birthday = #{cBirth}
and cid = #{cId}

select eid
from emp
where ename = #{cName}
and tel = #{cPhone}
and birthday = #{cBirth}
and eid = #{cId}

select aprvl_yn
from clinic
where bln = #{bln}

mapper.update:
update ceo
set pw = #{cPass}
where cid = #{cId}

update emp
set pw = #{cPass}
where eid = #{cId}

mapper.select:
select max(mseq) mseq
from mntr
where mid = #{cid}
and to_char(mdate,'yyyy-mm-dd') = #{date}
and bln = #{bln}
